// node.log(msg.payload);
var p = JSON.parse(msg.payload);
if(p.occupancy === undefined) return; // if state_cache is off, the field does not need to be included
flow.set('motion-bath', p.occupancy);
var m = {payload: p.occupancy ? 'ON' : 'OFF'};
if(p.occupancy){
    node.status({fill:"red", shape:"ring", text:"motion"});
    return [m, null];
} else {
    node.status({fill:"green", shape:"dot", text:"no motion"});
    return [null, m];
}

// node.log(msg.payload);
var p = JSON.parse(msg.payload);
if(p.contact === undefined) return;
flow.set('contact-bath', p.contact);
if(p.contact){
    node.status({fill:"red", shape:"ring", text:"closed"});
    if(flow.get('sonoff-bad') == 'ON')
        return {payload: p};
} else {
    node.status({fill:"green", shape:"dot", text:"open"});
    return {reset: true};
}

