var p = JSON.parse(msg.payload);
if(p.contact === undefined) return;
if(p.contact){
    node.status({fill:"green", shape:"dot", text:"closed"});
    if(!flow.get('contact-bath') && flow.get('sonoff-bath') == 'ON')
        return {payload: 'OFF'};
} else {
    node.status({fill:"red", shape:"ring", text:"open"});
